function desktop(el) {
    let newPosX = 0,
        newPosY = 0,
        startPosX = 0,
        startPosY = 0,
        parent = el.parentElement;


    // when the user clicks down on the element
    el.addEventListener('mousedown', function (e) {
        e.preventDefault();

        // get the starting position of the cursor
        startPosX = e.clientX;
        startPosY = e.clientY;

        document.addEventListener('mousemove', mouseMove);

        document.addEventListener('mouseup', function () {
            document.removeEventListener('mousemove', mouseMove);
        });
    });

    function mouseMove(e) {
        // calculate the new position
        newPosX = startPosX - e.clientX;
        newPosY = startPosY - e.clientY;
        // with each move we also want to update the start X and Y
        startPosX = e.clientX;
        startPosY = e.clientY;
        let main_view = document.querySelector('main').getBoundingClientRect(),
            client_height = document.documentElement.clientHeight,
            client_width = document.documentElement.clientWidth;
        console.log(main_view);
        // if (main_view.top >= 0 && main_view.left >= 0 && client_height >= main_view.bottom && client_width >= main_view.right) {
        parent.style.top = (parent.offsetTop - newPosY) + "px";
        parent.style.left = (parent.offsetLeft - newPosX) + "px";
        // }
    }

}

let black_rectangle = document.querySelector(".black-rectangle"),
    randomSentense = ["A man in Florida once threw a live alligator through a drive-thru window. A 24-year-old man named Joshua James was being handed his order", "Lorem ipsum dolor sit amet consectetur adipisicing elit. Sit magni consectetur qui vel beatae velit, etnumquam commodi nobis? Placeat qui incidunt reprehenderit officiis ea odio inventore fuga, omnis laborum"],
    clickIndex = 0,
    text_rectangle = document.querySelector(".rectangle"),
    length_measure = 2;
desktop(black_rectangle)

text_rectangle.addEventListener("click", () => {
    clickIndex < randomSentense.length ? document.querySelector(".rectangle p").innerHTML = randomSentense[clickIndex] : (clickIndex = 0, document.querySelector(".rectangle p").innerHTML = randomSentense[clickIndex]), clickIndex++
});
black_rectangle.addEventListener('click', () => {
    black_rectangle.style.width = `${150 + length_measure}px`;
    black_rectangle.style.height = `${70 + length_measure}px`;

    return length_measure > 10 ? length_measure -= 10 : length_measure += 15;
})